from collections import Counter
from typing import List

LONG_ASS_STRING = ""


class Solution:
    def longestPalindrome(self, s: str) -> str:
        """
        Given a string s, return the longest palindromic substring in s.
        """

        string_len = len(s)
        longest_palindrome = s[0]
        letter_counter = Counter(s)
        # Look at this counter, if there are letters that don't seem to allow a palindrome, then spilt and ignore?

        seen_counter = Counter()

        if len(set(s)) == 1:
            return s

        for i, letter in enumerate(s):
            seen_counter[letter] += 1

            if (
                letter_counter[letter] == 1
                or letter_counter[letter] - seen_counter[letter] <= 0
            ):
                continue

            for z, second_letter in enumerate(s[i + 1 :], i + 1):
                if letter == second_letter:
                    candidate = s[i : z + 1]
                    if "".join(reversed(candidate)) == candidate and len(
                        longest_palindrome
                    ) < len(candidate):
                        longest_palindrome = candidate
                        if string_len == len(longest_palindrome):
                            break

        return longest_palindrome


def _run(*args, **kwargs):
    return Solution().longestPalindrome(*args, **kwargs)


def test_works():

    assert _run("babad") == "bab"
    assert _run("cbbd") == "bb"
    assert _run("a") == "a"
    assert _run("ac") == "a"
    assert _run("ccc") == "ccc"


def test_fail():

    assert _run("bananas") == "anana"
    assert (
        _run(
            ""
        )
        == ""
    )


def test_long_ass_string():

    assert _run(LONG_ASS_STRING) == "a" * 500


